import React from 'react';
import '../App.css';

// Components
import HeroSection from '../components/HeroSection';
import GeneralService from '../components/GeneralService';
import CardFlag from '../components/CardFlag';
import Service1 from '../components/Service1';
import Service2 from '../components/Service2';
import Service3 from '../components/Service3';
import Footer from '../components/Footer';

// Bootstrap
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function Home() {
  // Estado para controlar qué contenido se muestra
  const [selectedCountry, setSelectedCountry] = useState(null);

  // Función para manejar el clic en la tarjeta CardFlag
  const handleCardClick = (countryName) => {
    // Define lógica para mostrar información específica según la tarjeta seleccionada
    // Aquí puedes usar un switch, if-else, o cualquier otra lógica para decidir qué mostrar
    if (countryName === 'Canadá') {
      // Por ejemplo, si se hace clic en Canadá, se establece el contenido específico para Canadá
      setSelectedCountry('canada'); // Puedes usar un identificador único para cada país
    } else if (countryName === 'Europa') {
      // Si se hace clic en Europa, se establece el contenido específico para Europa
      setSelectedCountry('europe');
    }
    // Agrega más casos según sea necesario para otros países
  };

  return (
    <>
      <HeroSection />
      <Container>
        <GeneralService />
      </Container>

      <Container fluid>
        <Row className="card-flag-container justify-content-center text-center">
          <Col xs={12} md={6} lg={3}>
            <CardFlag
              flagSrc="https://flagcdn.com/w320/ca.png"
              countryName="Canadá"
              onClick={() => handleCardClick('Canadá')} // Manejar clic en la tarjeta de Canadá
            />
          </Col>
          <Col xs={12} md={6} lg={3}>
            <CardFlag
              flagSrc="https://flagcdn.com/w320/eu.png"
              countryName="Europa"
              onClick={() => handleCardClick('Europa')} // Manejar clic en la tarjeta de Europa
            />
          </Col>
          {/* Agrega más tarjetas según sea necesario */}
        </Row>
      </Container>

      {/* Mostrar contenido específico según la tarjeta seleccionada */}
      {selectedCountry === 'canada' && <div>Contenido específico para Canadá</div>}
      {selectedCountry === 'europe' && <div>Contenido específico para Europa</div>}
      {/* Agrega más condiciones para otros países si es necesario */}
      <Footer />
    </>
  );
}

export default Home;
