import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEarthAmericas, faGraduationCap, faPlane, faHandshake } from '@fortawesome/free-solid-svg-icons';

const GeneralService = () => {
  const services = [
    { icon: faGraduationCap, title: 'Visas de Estudio', id: 'visa-estudios' },
    { icon: faEarthAmericas, title: 'Cursos de inglés', id: 'curso-ingles' },
    { icon: faPlane, title: 'Plan de Vacaciones', id: 'plan-vacaciones' },
    { icon: faHandshake, title: 'Asesoría Migratoria', id: 'asesoria' },
  ];

  const titleStyle = {
    color: '#393459',
  };

  const handleServiceClick = (id) => {
    const element = document.getElementById(id);
    if (element) {
      const container = document.querySelector('.service-list');
      if (container) {
        container.scrollTop = element.offsetTop - container.offsetTop;
      }
    }
  };

  return (
    <div className="text-center p-4">
      <div className="general-service">
        <h2 style={{ backgroundColor: '#94E1F2', borderRadius: '10px', padding: '5px', color: 'white' }}>Nuestros servicios</h2>
        <p>Te apoyamos con el primer paso para obtener una de las siguientes Visas</p>
        <div className="service-list" style={{ height: '300px', overflowY: 'scroll' }}> {/* Ajusta la altura y el overflow */}
          {services.map((service, index) => (
            <div key={index} id={service.id} className="service-item" onClick={() => handleServiceClick(service.id)}>
              <FontAwesomeIcon icon={service.icon} size="3x" />
              <h4 style={titleStyle}>{service.title}</h4>
              {/* Contenido adicional de la tarjeta */}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default GeneralService;
