import React, { useState } from 'react';

const ConfirmationMessage = ({ show }) => {
  const [isVisible, setIsVisible] = useState(show);

  // Manejar cambios en las propiedades para mostrar u ocultar el mensaje
  React.useEffect(() => {
    setIsVisible(show);
  }, [show]);

  return (
    <div style={{ display: isVisible ? 'block' : 'none' }}>
      <h2>¡Formulario enviado con éxito!</h2>
      <p>Gracias por contactarnos. Recibimos tu solicitud correctamente.</p>
      {/* Puedes agregar más contenido o redireccionar a otra página desde aquí */}
    </div>
  );
};

export default ConfirmationMessage;
